export const CheckIcon = ({
  checked,
  className,
}: {
  checked: boolean
  className?: string
}) => {
  if (!checked) {
    return (
      <div className="p-2px bg-transparent border border-border-strong  rounded-md">
        <div
          className={`w-[10px] h-[10px] rounded-sm bg-transparent  ${className || ''}`}
        />
      </div>
    )
  }

  return (
    <div className="p-2px border border-border-high rounded-md">
      <div
        className={`w-[10px] h-[10px] rounded-sm bg-brand-primary  ${className || ''}`}
      />
    </div>
  )
}

